def delete():
    with open("Files/Skills/Skill.json", 'r') as fols:
        skills=json.load(fols)

    del skills[name]

    with open("Files/Skills/Skill.json", 'r') as fols:
        json.dump(skills, fols, indent=6)

    subprocess.Popen(['python', 'Anime Version/Skills Tab/build/gui.py'])

    window.quit()

